                VESAVBEConsole.mWidth = (int)ConsoleMode.Mode800x600.Rows;
                VESAVBEConsole.mCols = (int)ConsoleMode.Mode800x600.Cols;
                VESAVBEConsole.mHeight = (int)ConsoleMode.Mode800x600.Cols;
                VESAMode = "Mode800x600";
